glsl1.vert:
precision highp float;
uniform highp mat4 cc_matViewProj;
uniform highp mat4 cc_matWorld;
attribute vec3 a_position;
attribute vec2 a_texCoord;
varying vec2 v_uv;
void main () {
  vec4 worldPos = cc_matWorld * vec4(a_position, 1.0);
  gl_Position = cc_matViewProj * worldPos;
  v_uv = a_texCoord;
}

glsl1.frag:
precision highp float;
varying vec2 v_uv;
uniform sampler2D mainTexture;
  uniform vec4 mainColor;
void main() {
  vec4 c = texture2D(mainTexture, v_uv);
  c *= mainColor;
  gl_FragColor = c;
}
