}

glsl1.vert:
precision highp float;
uniform highp mat4 cc_matViewProj;
uniform highp mat4 cc_matWorld;
attribute vec3 a_position;
attribute vec3 a_normal;
attribute vec2 a_texCoord;
attribute vec4 a_color;
   uniform float u_Inflate;
varying vec2 v_uv;
varying vec4 v_color;
void main () {
  vec3 pos = a_position + a_normal * u_Inflate;
  vec4 worldPos = cc_matWorld * vec4(pos, 1.0);
  gl_Position = cc_matViewProj * worldPos;
  v_uv = a_texCoord;
  v_color = a_color;
}

glsl1.frag:
precision highp float;
varying vec2 v_uv;
varying vec4 v_color;
uniform sampler2D u_OverlayTex;
  uniform vec4 u_BiomeColor;
void main() {
  vec4 c = texture2D(u_OverlayTex, v_uv);
  c.rgb *= u_BiomeColor.rgb;
  if (c.a <= 0.01) discard;
  gl_FragColor = c;
}
